
public class MathematicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x = 4;
        int y = 2;
        int z;
        
        z = x+y;
        
        System.out.println(z);
        
        z = x-y;
        
        System.out.println(z);
        
        z = x*y;
        
        System.out.println(z);
        
        z = x/y;
        
        System.out.println(z);
        
        z = x%y;
        
        System.out.println(z);
        
        

	}

}
